import { StatusBar } from 'expo-status-bar';
import { SafeAreaProvider } from 'react-native-safe-area-context';
import { Camera } from 'expo-camera';
import React, { useState, useEffect, useRef } from 'react';
import { Text, View, TouchableOpacity, StyleSheet } from 'react-native';

export default function App() {
  const [hasPermission, setHasPermission] = useState(null);
  const [cameraRef, setCameraRef] = useState(null);
  const [usingCamera, setUsingCamera] = useState(null);
  const [type, setType] = useState(Camera.Constants.Type.back);
  useEffect(() => {
      (async () => {
        const { status } = await Camera.requestCameraPermissionsAsync();
        setHasPermission(status === 'granted');
      })();
    }, []);

  if (hasPermission === null) {
      return <View />;
    }
    if (hasPermission === false) {
      return <Text>No access to camera</Text>;
    }
    return (
      <View style={{ flex: 1 }}>
        { (usingCamera == true) && (
        <Camera style={{ flex: 1 }} type={type} ref={ref => {
          setCameraRef(ref) ;
        }}>
          <View
            style={styles.pictureView}>
            <TouchableOpacity
              style={styles.textCommand}
              onPress={() => {
                setType(
                  type === Camera.Constants.Type.back
                    ? Camera.Constants.Type.front
                    : Camera.Constants.Type.back
                );
              }}>
              <Text style={{ fontSize: 18, marginBottom: 10, color: 'white' }}> Flip </Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.textCommand} onPress={() => setUsingCamera(false)}>
              <Text style={{ fontSize: 18, marginBottom: 10, color: 'white' }}> Close </Text>
            </TouchableOpacity>
            <TouchableOpacity style={{alignSelf: 'center'}} onPress={async() => {
              if(cameraRef){
                let photo = await cameraRef.takePictureAsync();
                console.log('photo', photo);
              }
            }}>
              <View style={{ 
                borderWidth: 2,
                borderRadius:"50%",
                borderColor: 'white',
                height: 50,
                width:50,
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center'}}
              >
                <View style={{
                  borderWidth: 2,
                  borderRadius:"50%",
                  borderColor: 'white',
                  height: 40,
                  width:40,
                  backgroundColor: 'white'}} >
                </View>
              </View>
            </TouchableOpacity>
          </View>
        </Camera>
        )}
        { (!usingCamera) &&
          <View style={styles.button}>
            <button onClick={() => setUsingCamera(true)}> Use Camera </button>
          </View>
        }
      </View>
    );
  }
  const styles = StyleSheet.create({
    textCommand: {
      alignSelf: 'flex-end'
    },
    pictureView: {
      flex: 1,
      backgroundColor: 'transparent',
      justifyContent: 'flex-end'
    },
    button: {
      flexDirection: "row",
      justifyContent: 'center',
      alignItems: "center",
    }
  });
